mapper.select:
select permission_id as id, permission_name as name, icon, code, iorder, parent_id as "parentId", permission_type as "permissionType", link_url as "linkUrl", display from v_sys_permission vsp where 1=1
and permission_name like #{permissionName}
and permission_type = #{permissionType}
and display = #{display}
and not exists( select permission_id from ( with recursive r as( select * from v_sys_permission where permission_id = #{permissionId} union all select p.* from v_sys_permission p, r where r.permission_id = p.parent_id ) select permission_id from r ) as tmp where tmp.permission_id = vsp.permission_id )
order by iorder

mapper.delete:
delete from sys_menu where menu_id in(
    with recursive r as(
        select menu_id, parent_id from sys_menu where menu_id in

            #{menuId}

        union all
        select p.menu_id, p.parent_id from sys_menu p, r where r.menu_id = p.parent_id
    )
    select menu_id from r
)
