mapper.select:
select

from sys_user su
where su.loginname like #{loginName}

select su.*, coalesce( group_concat(so.organization_name) string_agg(so.organization_name, ',' ) , '' ) as organization_name, va1.attr_name as userSexName, va2.attr_name as lockedName from sys_user su left join sys_user_organization suo on su.id = suo.user_id left join sys_organization so on suo.organization_id = so.organization_id left join ( select attr_value, attr_name from v_sys_dict_attr where dict_code = 'user-sex' ) va1 on va1.attr_value = su.user_sex left join ( select attr_value, attr_name from v_sys_dict_attr where dict_code = 'user-status' ) va2 on va2.attr_value = concat(su.locked, '') where 1=1
and su.username like concat('%', #{username}, '%')
and su.user_sex like concat('%', #{userSex}, '%')
and suo.organization_id in(
#{organizationId}
#{organizationId}
)
group by su.id, usersexname, lockedname
